package Basic100;
import java.util.Scanner;

public class Basic_1053 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner stdin = new Scanner(System.in);
		int i = stdin.nextInt();
		stdin.close();
		if(i==0)
			System.out.println("1");
		else if(i==1)
			System.out.println("0");
	}

}
